import Popup from './SignIn';
import React, { useState, useEffect } from 'react';
import decode from 'jwt-decode';
import { useDispatch } from 'react-redux';
import { useNavigate, useLocation } from 'react-router-dom';
import * as actionType from "../../constants/actionTypes"

const Button = () => {
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    // const dispatch = useDispatch();
    // const location = useLocation();
    // const history = useNavigate();

    // const logout = () => {
    //     dispatch({ type: actionType.LOGOUT });
    //     history('/auth');
    //     // history.push('/auth');
    
    //     setUser(null);
    //   };

    // useEffect(() => {
    //     const token = user?.token;
    
    //     if (token) {
    //       const decodedToken = decode(token);
    
    //       if (decodedToken.exp * 1000 < new Date().getTime()) logout();
    //     }
    
    //     setUser(JSON.parse(localStorage.getItem('profile')));
    //   }, [location]);

  const [isPopupOpen, setIsPopupOpen] = useState(false);

  function handleButtonClick() {
    setIsPopupOpen(!isPopupOpen);
  }

  return (
    <>
    <div>
      {/* <button onClick={handleButtonClick}>Sign In</button> */}
      {user?.result ? (
        <div>
          <img alt={user?.result.name} src={user?.result.imageUrl}>{user?.result.name.charAt(0)}</img>
          <h3>{user?.result.name}</h3>
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <button onClick={handleButtonClick}>Sign In</button>
      )}
      {isPopupOpen && <Popup onClose={handleButtonClick} />}
    </div>
    </>
  );
}

export default Button;
