import './SignUp.css'
import { createPost , updatePost} from '../../actions/posts';

const SignUp = ({currentId, setCurrentId}) => {

    const post = useSelector((state) => currentId ? state.posts.find((p) => p._id === currentId ) : null);

    useEffect(()=>{
        if(post) setPostData(post);
    }, [post])

    const [postData, setPostData] = useState({
        creator: '',
        title: '',
        message: '',
        tags: '',
        selectedFile: ''

    });

    const dispatch = useDispatch();

    const clear = () => {

        setCurrentId(null);

        setPostData({
            creator: '',
            title: '',
            message: '',
            tags: '',
            selectedFile: ''
        });

    }

    const handleSubmit = (e) => {
        e.preventDefault();
        if(currentId){
            dispatch(updatePost(currentId, postData));
        }else {
            dispatch(createPost(postData))
        }

        clear();
        
    }

    return(
        <>
        <form onSubmit={handleSubmit}>
            <div className="form">
            <div className="form-body">
                <div className="username">
                    <label className="form__label" for="firstName">First Name </label>
                    <input 
                    className="form__input" 
                    type="text" 
                    name="firstName"
                    id="firstName" 
                    placeholder="First Name"
                    value={postData.creator}
                    onChange={(e) => setPostData( {...postData, creator: e.target.value })}
                    />
                </div>
                <div className="lastname">
                    <label className="form__label" for="lastName">Last Name </label>
                    <input
                    className="form__input"  
                    type="text" 
                    name="lastName" 
                    id="lastName"  
                    placeholder="LastName" 
                    value={postData.title} 
                    onChange={(e) => setPostData( {...postData, title: e.target.value })}
                />
                </div>
                <div className="cnic">
                    <label className="form__label" for="cnic">CNIC </label>
                    <input  
                    type="text" 
                    name="cnic" 
                    id="cnic"  
                    className="form__input"
                    placeholder="cnic"
                    value={postData.message} 
                    onChange={(e) => setPostData( {...postData, message: e.target.value })}
                    />
                </div>
                <div className="work">
                    <label className="form__label" for="work">Work </label>
                    <input  
                    type="text" 
                    name="work" 
                    id="work"  
                    className="form__input"
                    placeholder="work"
                    value={postData.tags} 
                    onChange={(e) => setPostData( {...postData, tags: e.target.value.split(',') })}
                    />
                </div>
                {/* <div className="contact">
                    <label className="form__label" for="contact">Contact No </label>
                    <input  type="text" name="" id="contact"  className="form__input"placeholder="contact no"/>
                </div>
                <div className="salary">
                    <label className="form__label" for="salary">Salary Range </label>
                    <input  type="text" name="" id="salary"  className="form__input"placeholder="Salary Range"/>
                </div>

                <div className="description">
                    <label className="form__label" for="description">Description </label>
                    <input  type="text" name="" id="description"  className="form__input"placeholder="description"/>
                </div>
                <div className="password">
                    <label className="form__label" for="password">Password </label>
                    <input className="form__input" type="password"  id="password" placeholder="Password"/>
                </div>
                <div className="confirm-password">
                    <label className="form__label" for="confirmPassword">Confirm Password </label>
                    <input className="form__input" type="password" id="confirmPassword" placeholder="Confirm Password"/>
                </div> */}
            </div>
            <div class="footer">
                <button type="submit" class="btn">Register</button>
            </div>
        </div>
      </form>  
        </>
    );
}

export default SignUp;
